import React, { Component } from 'react';
import { Switch, Route, Redirect } from 'react-router-dom';
import Search from '../Search/Search';
import WordOfTheDay from '../WordOfTheDay/WordOfTheDay';
import Learn from '../Learn/Learn';
import SignIn from '../../Containers/SignIn/SignIn';
import WhatIsCantoTalk from '../../Components/WhatIsCantoTalk/WhatIsCantoTalk';
import Profile from '../Profile/Profile';
import Contact from '../Contact/Contact';
import PrivacyPolicy from '../../Components/PrivacyPolicy/PrivacyPolicy';
import AddNewEntry from '../AddNewEntry/AddNewEntry'
import { connect } from 'react-redux';
import { CSSTransition, TransitionGroup } from 'react-transition-group';
import { routes } from '../../Routing/constants';

const mapStateToProps = (state, ownProps) => {
  return {
    location: state.router.location,
  }
}

class MainView extends Component {

	shouldComponentUpdate(newProps) {
		const pathName = this.props.location.pathname
		const newPathName = newProps.location.pathname
		if (pathName === newPathName) {
			return false
		} else {
			return true
		}
	}

	render() {
		const { 
			LOGIN, 
			REGISTER,
			VERIFY,
			RESET,
			WHAT, 
			CONTACT,
			SEARCH, 
			WORD_OF_THE_DAY, 
			LEARN, 
			PROFILE,
			PRIVACY,
			ADD,
			TRANSITION 
		} = routes;
		const { location, location: { key } } = this.props

		return (
			<div className='main-view' style={{position: 'absolute'}}>
				<Switch location={location}>
					<Route 
						path={[LOGIN, REGISTER, VERIFY, RESET]} 
						render={()=>(
							<TransitionGroup className='page-trans over-flow'>
								<CSSTransition
									key={key}
						            timeout={TRANSITION}
						            classNames="fade"
					            >
				                    <SignIn />
					            </CSSTransition>
							</TransitionGroup>
						)}
					/>
					<Route 
						path={PROFILE} 
						render={()=>(
							<TransitionGroup className='page-trans over-flow'>
								<CSSTransition
									key={key}
						            timeout={TRANSITION}
						            classNames="fade"
					            >
				                    <Profile />
					            </CSSTransition>
							</TransitionGroup>
						)}
					/>
					<Route 
						path={WHAT} 
						render={()=>(
							<TransitionGroup className='page-trans over-flow'>
								<CSSTransition
									key={key}
						            timeout={TRANSITION}
						            classNames="fade"
					            >
				                    <WhatIsCantoTalk />
					            </CSSTransition>
							</TransitionGroup>
						)}
					/>
					<Route 
						path={CONTACT} 
						render={()=>(
							<TransitionGroup className='page-trans over-flow'>
								<CSSTransition
									key={key}
						            timeout={TRANSITION}
						            classNames="fade"
					            >
				                    <Contact />
					            </CSSTransition>
							</TransitionGroup>
						)}
					/>
					<Route 
						path={SEARCH} 
						render={()=>(
							<TransitionGroup className='page'>
								<CSSTransition
									key={key}
						            timeout={TRANSITION}
						            classNames="fade"
					            >
					            	<Search />
					            </CSSTransition>
							</TransitionGroup>
						)}
					/>
					<Route 
						path={WORD_OF_THE_DAY} 
						render={()=>(
							<TransitionGroup className='page'>
								<CSSTransition
									key={key}
						            timeout={TRANSITION}
						            classNames="fade"
					            >
					            	<WordOfTheDay />
					            </CSSTransition>
							</TransitionGroup>
						)}
					/>
					<Route 
						path={LEARN} 
						render={()=>(
							<TransitionGroup className='page'>
								<CSSTransition
									key={key}
						            timeout={TRANSITION}
						            classNames="fade"
					            >
					            	<Learn />
					            </CSSTransition>
							</TransitionGroup>
						)}
					/>
					<Route 
						path={PRIVACY} 
						render={()=>(
							<TransitionGroup className='page over-flow'>
								<CSSTransition
									key={key}
						            timeout={TRANSITION}
						            classNames="fade"
					            >
					            	<PrivacyPolicy />
					            </CSSTransition>
							</TransitionGroup>
						)}
					/>
					<Route 
						path={ADD} 
						render={()=>(
							<TransitionGroup className='page over-flow'>
								<CSSTransition
									key={key}
						            timeout={TRANSITION}
						            classNames="fade"
					            >
					            	<AddNewEntry />
					            </CSSTransition>
							</TransitionGroup>
						)}
					/>
					<Route 
						render={()=>(
							<Redirect to={SEARCH} />
						)}
					/>
				</Switch>
			</div>
		);
	}

}

export default connect(mapStateToProps, null)(MainView);
